DROP TABLE IF EXISTS `<%?prefix%>catalog_bookstore_articles`; CREATE TABLE IF NOT EXISTS `<%?prefix%>catalog_bookstore_articles` ( `articleId` int(11) unsigned NOT NULL AUTO_INCREMENT, `status` tinyint(1) NOT NULL, `title` varchar(255) COLLATE utf8_unicode_ci NOT NULL DEFAULT '', `subtitle` text COLLATE utf8_unicode_ci NOT NULL, `description` text COLLATE utf8_unicode_ci NOT NULL, `recension` text COLLATE utf8_unicode_ci NOT NULL, `publication` varchar(10) COLLATE utf8_unicode_ci NOT NULL, `size` varchar(200) COLLATE utf8_unicode_ci NOT NULL DEFAULT '', `digestion` varchar(200) COLLATE utf8_unicode_ci NOT NULL DEFAULT '', `weight` float(6,4) NOT NULL, `isn` varchar(100) COLLATE utf8_unicode_ci NOT NULL, `series` tinyint(1) unsigned DEFAULT NULL, `price` varchar(10) COLLATE utf8_unicode_ci DEFAULT '0', `cover` varchar(250) COLLATE utf8_unicode_ci DEFAULT NULL, `language` varchar(20) COLLATE utf8_unicode_ci NOT NULL DEFAULT 'de', `new` tinyint(1) unsigned DEFAULT '0', `createdAt` decimal(12,0) unsigned NOT NULL, `modifiedAt` decimal(12,0) unsigned DEFAULT NULL, PRIMARY KEY (`articleId`), KEY `isbn` (`isn`), KEY `language` (`language`), KEY `new` (`new`), KEY `series` (`series`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>catalog_bookstore_article_authors`; CREATE TABLE IF NOT EXISTS `<%?prefix%>catalog_bookstore_article_authors` ( `articleAuthorId` int(11) unsigned NOT NULL AUTO_INCREMENT, `articleId` int(11) unsigned NOT NULL, `authorId` int(11) unsigned NOT NULL, `editor` tinyint(1) NOT NULL, PRIMARY KEY (`articleAuthorId`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>catalog_bookstore_article_categories`; CREATE TABLE IF NOT EXISTS `<%?prefix%>catalog_bookstore_article_categories` ( `articleCategoryId` int(11) unsigned NOT NULL AUTO_INCREMENT, `articleId` int(11) unsigned NOT NULL, `categoryId` int(11) unsigned NOT NULL, `volume` varchar(20) COLLATE utf8_unicode_ci NOT NULL DEFAULT '', PRIMARY KEY (`articleCategoryId`), KEY `article_id` (`articleId`,`categoryId`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>catalog_bookstore_article_documents`; CREATE TABLE IF NOT EXISTS `<%?prefix%>catalog_bookstore_article_documents` ( `articleDocumentId` int(11) unsigned NOT NULL AUTO_INCREMENT, `articleId` int(11) unsigned NOT NULL, `status` tinyint(1) NOT NULL, `type` tinyint(1) unsigned NOT NULL, `url` varchar(255) COLLATE utf8_unicode_ci NOT NULL, `title` varchar(255) COLLATE utf8_unicode_ci NOT NULL, PRIMARY KEY (`articleDocumentId`), KEY `article_id` (`articleId`), KEY `status` (`status`,`type`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>catalog_bookstore_article_reviews`; CREATE TABLE IF NOT EXISTS `<%?prefix%>catalog_bookstore_article_reviews` ( `articleReviewId` int(11) unsigned NOT NULL AUTO_INCREMENT, `articleId` int(11) unsigned DEFAULT NULL, `content` text COLLATE utf8_unicode_ci, `excerpt` text COLLATE utf8_unicode_ci NOT NULL, PRIMARY KEY (`articleReviewId`), KEY `article_id` (`articleId`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>catalog_bookstore_article_tags`; CREATE TABLE IF NOT EXISTS `<%?prefix%>catalog_bookstore_article_tags` ( `articleTagId` int(10) unsigned NOT NULL AUTO_INCREMENT, `articleId` int(10) unsigned NOT NULL, `tag` varchar(30) COLLATE utf8_unicode_ci NOT NULL, PRIMARY KEY (`articleTagId`), KEY `articleId` (`articleId`), KEY `tag` (`tag`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>catalog_bookstore_authors`; CREATE TABLE IF NOT EXISTS `<%?prefix%>catalog_bookstore_authors` ( `authorId` int(11) unsigned NOT NULL AUTO_INCREMENT, `lastname` varchar(200) COLLATE utf8_unicode_ci NOT NULL, `firstname` varchar(100) COLLATE utf8_unicode_ci NOT NULL, `image` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL, `reference` varchar(250) COLLATE utf8_unicode_ci DEFAULT NULL, `description` text COLLATE utf8_unicode_ci, PRIMARY KEY (`authorId`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>catalog_bookstore_categories`; CREATE TABLE IF NOT EXISTS `<%?prefix%>catalog_bookstore_categories` ( `categoryId` int(11) unsigned NOT NULL AUTO_INCREMENT, `parentId` int(11) unsigned NOT NULL, `visible` tinyint(1) unsigned NOT NULL, `issn` varchar(20) COLLATE utf8_unicode_ci NOT NULL, `rank` tinyint(2) NOT NULL, `publisher` varchar(200) COLLATE utf8_unicode_ci NOT NULL, `label_de` varchar(220) COLLATE utf8_unicode_ci NOT NULL, `label_en` varchar(220) COLLATE utf8_unicode_ci DEFAULT NULL, `label_it` varchar(220) COLLATE utf8_unicode_ci DEFAULT NULL, `label_former` varchar(250) COLLATE utf8_unicode_ci NOT NULL, PRIMARY KEY (`categoryId`), KEY `parentId` (`parentId`,`visible`,`issn`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>catalog_bookstore_states`; CREATE TABLE IF NOT EXISTS `<%?prefix%>catalog_bookstore_states` ( `statusId` tinyint(2) unsigned NOT NULL AUTO_INCREMENT, `title` varchar(200) COLLATE utf8_unicode_ci NOT NULL DEFAULT '', `available` tinyint(1) unsigned NOT NULL DEFAULT '0', `rank` tinyint(2) NOT NULL DEFAULT '0', PRIMARY KEY (`statusId`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

