module.sql:
DROP TABLE IF EXISTS `<%?prefix%>blog_categories`; CREATE TABLE IF NOT EXISTS `<%?prefix%>blog_categories` ( `categoryId` int(11) NOT NULL AUTO_INCREMENT, `status` tinyint(1) unsigned NOT NULL DEFAULT '0', `language` char(2) COLLATE utf8_unicode_ci DEFAULT NULL, `title` varchar(30) COLLATE utf8_unicode_ci NOT NULL, `content` mediumint(9) DEFAULT NULL, `nrViews` int(11) unsigned DEFAULT '0', `createdAt` decimal(12,0) unsigned NOT NULL, `modifiedAt` decimal(12,0) unsigned DEFAULT NULL, `viewedAt` decimal(12,0) unsigned DEFAULT NULL, PRIMARY KEY (`categoryId`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>blog_comments`; CREATE TABLE IF NOT EXISTS `<%?prefix%>blog_comments` ( `commentId` int(11) unsigned NOT NULL AUTO_INCREMENT, `postId` int(11) unsigned NOT NULL, `authorId` int(11) unsigned DEFAULT '0', `status` tinyint(1) DEFAULT '0', `language` char(2) COLLATE utf8_unicode_ci NOT NULL, `username` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL, `email` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL, `title` char(100) COLLATE utf8_unicode_ci NOT NULL, `content` text COLLATE utf8_unicode_ci NOT NULL, `createdAt` decimal(12,0) NOT NULL, PRIMARY KEY (`commentId`), KEY `postId` (`postId`), KEY `authorId` (`authorId`), KEY `status` (`status`), KEY `language` (`language`), KEY `username` (`username`), KEY `email` (`email`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>blog_posts`; CREATE TABLE IF NOT EXISTS `<%?prefix%>blog_posts` ( `postId` int(11) unsigned NOT NULL AUTO_INCREMENT, `categoryId` int(11) unsigned NOT NULL, `authorId` int(11) unsigned NOT NULL, `status` tinyint(1) DEFAULT '0', `language` char(2) COLLATE utf8_unicode_ci NOT NULL, `title` varchar(100) COLLATE utf8_unicode_ci NOT NULL, `abstract` mediumtext COLLATE utf8_unicode_ci, `content` text COLLATE utf8_unicode_ci NOT NULL, `nrViews` int(11) NOT NULL DEFAULT '0', `createdAt` decimal(12,0) NOT NULL, `modifiedAt` decimal(12,0) DEFAULT NULL, `viewedAt` decimal(12,0) DEFAULT NULL, PRIMARY KEY (`postId`), KEY `categoryId` (`categoryId`), KEY `authorId` (`authorId`), KEY `status` (`status`), KEY `language` (`language`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

DROP TABLE IF EXISTS `<%?prefix%>blog_categories`;
DROP TABLE IF EXISTS `<%?prefix%>blog_comments`;
DROP TABLE IF EXISTS `<%?prefix%>blog_posts`;
