module.sql:
DROP TABLE IF EXISTS `<%?prefix%>forum_posts`; CREATE TABLE IF NOT EXISTS `<%?prefix%>forum_posts` ( `postId` int(11) unsigned NOT NULL AUTO_INCREMENT, `threadId` int(11) unsigned NOT NULL, `parentId` int(11) unsigned DEFAULT NULL, `authorId` int(11) unsigned DEFAULT NULL, `type` tinyint(1) unsigned DEFAULT '0', `status` tinyint(1) DEFAULT '0', `content` text COLLATE utf8_unicode_ci NOT NULL, `createdAt` decimal(12,0) unsigned NOT NULL, `modifiedAt` decimal(12,0) unsigned DEFAULT NULL, PRIMARY KEY (`postId`), KEY `threadId` (`threadId`), KEY `parentId` (`parentId`), KEY `authorId` (`authorId`), KEY `type` (`type`), KEY `status` (`status`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>forum_threads`; CREATE TABLE IF NOT EXISTS `<%?prefix%>forum_threads` ( `threadId` int(11) unsigned NOT NULL AUTO_INCREMENT, `parentId` int(11) unsigned DEFAULT NULL, `topicId` int(11) unsigned DEFAULT NULL, `authorId` int(11) unsigned DEFAULT NULL, `type` tinyint(1) unsigned DEFAULT '0', `status` tinyint(1) DEFAULT '0', `title` varchar(100) COLLATE utf8_unicode_ci NOT NULL, `createdAt` decimal(12,0) unsigned NOT NULL, `modifiedAt` decimal(12,0) unsigned DEFAULT NULL, PRIMARY KEY (`threadId`), KEY `parentId` (`parentId`), KEY `topicId` (`topicId`), KEY `authorId` (`authorId`), KEY `type` (`type`), KEY `status` (`status`), KEY `title` (`title`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>forum_topics`; CREATE TABLE IF NOT EXISTS `<%?prefix%>forum_topics` ( `topicId` int(11) unsigned NOT NULL AUTO_INCREMENT, `parentId` int(11) unsigned DEFAULT NULL, `authorId` int(11) unsigned DEFAULT NULL, `type` tinyint(1) unsigned DEFAULT '0', `status` tinyint(1) DEFAULT '0', `title` varchar(200) COLLATE utf8_unicode_ci NOT NULL, `description` text COLLATE utf8_unicode_ci, `createdAt` decimal(12,0) unsigned NOT NULL, `modifiedAt` decimal(12,0) unsigned DEFAULT NULL, PRIMARY KEY (`topicId`), KEY `parentId` (`parentId`), KEY `authorId` (`authorId`), KEY `type` (`type`), KEY `status` (`status`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

DROP TABLE IF EXISTS `<%?prefix%>forum_posts`;
DROP TABLE IF EXISTS `<%?prefix%>forum_threads`;
DROP TABLE IF EXISTS `<%?prefix%>forum_topics`;
