module.hook:
$moduleConfig	= $env->getConfig()->getAll( 'module.js_ace.', TRUE );
$env->getConfig()->get( 'path.scripts' );

if( $moduleConfig->get( 'CDN' ){
//	$uri = $moduleConfig->get( 'CDN.URI' ).$version.'/ace.js';
//	$context->js->addUrl( $uri );
}
else{
	$context->js->addUrl( $pathJs.'ace/ace.js' );
}
$context->js->addUrl( $pathJs.'module.js.ace.js' );
if( !$moduleConfig->get( 'auto' ) )
	return;
$script	= '
jQuery("'.$moduleConfig->get( 'auto.selector'").applyAuto();
';
$context->js->addScriptOnReady( $script );
