module.hook:
$moduleConfig	= $env->getConfig()->getAll( 'module.js_ace.', TRUE );
$pathJs			= $env->getConfig()->get( 'path.scripts' );
$version		= $moduleConfig->get( 'source.version' );
$cdn			= $moduleConfig->get( 'source.CDN' );
$words			= $env->getLanguage()->getWords( 'ace' );

$uri	= 'https://cdnjs.cloudflare.com/ajax/libs/ace/'.$version.'/';
if( $cdn == 'jsDelivr' )
	$uri	= 'https://cdn.jsdelivr.net/ace/'.$version.'/noconflict/';

$context->js->addUrl( $uri.'ace.js' );
$context->js->addUrl( $pathJs.'module.js.ace.js' );

$durations		= $moduleConfig->getAll( 'auto.save.duration.', FALSE );
$overlayLabels	= $words['overlay-labels'];
$context->js->addScriptOnReady( '
ModuleAceAutoSave.options.words = '.json_encode( $overlayLabels ).';
ModuleAceAutoSave.options.durations = '.json_encode( $durations ).';' );

if( !$moduleConfig->get( 'auto' ) )
	return;
$context->js->addScriptOnReady( '
ModuleAce.verbose = '.json_encode( $moduleConfig->get( 'auto.verbose' ) ).';
ModuleAce.applyAuto("'.$moduleConfig->get( 'auto.selector' ).'");' );
