DROP TABLE IF EXISTS `<%?prefix%>mail_group_members`; CREATE TABLE IF NOT EXISTS `<%?prefix%>mail_group_members` ( `mailGroupMemberId` int(10) unsigned NOT NULL AUTO_INCREMENT, `mailGroupId` int(10) unsigned NOT NULL, `roleId` int(10) unsigned NOT NULL, `status` tinyint(1) unsigned NOT NULL, `address` varchar(255) COLLATE utf8_unicode_ci NOT NULL, `title` varchar(255) COLLATE utf8_unicode_ci NOT NULL, `createdAt` decimal(12,0) unsigned NOT NULL, `modifiedAt` decimal(12,0) unsigned NOT NULL, PRIMARY KEY (`mailGroupMemberId`), KEY `mailGroupId` (`mailGroupId`), KEY `status` (`status`), KEY `address` (`address`), KEY `roleId` (`roleId`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>mail_group_roles`; CREATE TABLE IF NOT EXISTS `<%?prefix%>mail_group_roles` ( `mailGroupRoleId` int(10) unsigned NOT NULL AUTO_INCREMENT, `status` tinyint(1) unsigned NOT NULL, `rank` tinyint(1) unsigned NOT NULL, `title` varchar(50) COLLATE utf8_unicode_ci NOT NULL, `read` tinyint(1) unsigned NOT NULL, `write` tinyint(1) unsigned NOT NULL, `createdAt` decimal(12,0) unsigned NOT NULL, `modifiedAt` decimal(12,0) unsigned NOT NULL, PRIMARY KEY (`mailGroupRoleId`), UNIQUE KEY `title` (`title`), KEY `status` (`status`), KEY `rank` (`rank`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
