module.sql:
DROP TABLE IF EXISTS `<%?prefix%>oauth_access_tokens`; CREATE TABLE IF NOT EXISTS `<%?prefix%>oauth_access_tokens` ( `oauthAccessTokenId` int(10) unsigned NOT NULL AUTO_INCREMENT, `oauthApplicationId` int(10) unsigned NOT NULL, `userId` int(11) unsigned DEFAULT NULL, `token` varchar(32) COLLATE utf8_unicode_ci NOT NULL, `scope` text COLLATE utf8_unicode_ci NOT NULL, `createdAt` decimal(12,0) unsigned NOT NULL, PRIMARY KEY (`oauthAccessTokenId`), KEY `oauthApplicationId` (`oauthApplicationId`), KEY `userId` (`userId`), KEY `token` (`token`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>oauth_applications`; CREATE TABLE IF NOT EXISTS `<%?prefix%>oauth_applications` ( `oauthApplicationId` int(11) NOT NULL AUTO_INCREMENT, `userId` int(10) unsigned NOT NULL, `type` tinyint(1) unsigned NOT NULL, `status` tinyint(1) NOT NULL DEFAULT '0', `clientId` varchar(200) COLLATE utf8_unicode_ci NOT NULL, `clientSecret` varchar(32) COLLATE utf8_unicode_ci NOT NULL, `title` varchar(100) COLLATE utf8_unicode_ci NOT NULL, `description` text COLLATE utf8_unicode_ci NOT NULL, `url` varchar(250) COLLATE utf8_unicode_ci NOT NULL, `createdAt` decimal(12,0) unsigned NOT NULL, `modifiedAt` decimal(12,0) unsigned NOT NULL, PRIMARY KEY (`oauthApplicationId`), UNIQUE KEY `clientId` (`clientId`), KEY `userId` (`userId`), KEY `type` (`type`), KEY `status` (`status`), KEY `clientSecret` (`clientSecret`), KEY `url` (`url`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>oauth_codes`; CREATE TABLE IF NOT EXISTS `<%?prefix%>oauth_codes` ( `oauthCodeId` int(11) NOT NULL AUTO_INCREMENT, `oauthApplicationId` varchar(200) COLLATE utf8_unicode_ci NOT NULL, `userId` int(11) unsigned NOT NULL, `redirectUri` varchar(250) COLLATE utf8_unicode_ci NOT NULL, `code` varchar(32) COLLATE utf8_unicode_ci NOT NULL, `scope` text COLLATE utf8_unicode_ci NOT NULL, `createdAt` decimal(12,0) unsigned NOT NULL, PRIMARY KEY (`oauthCodeId`), KEY `oauthApplicationId` (`oauthApplicationId`), KEY `userId` (`userId`), KEY `redirectUri` (`redirectUri`), KEY `code` (`code`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>oauth_refresh_tokens`; CREATE TABLE IF NOT EXISTS `<%?prefix%>oauth_refresh_tokens` ( `oauthRefreshTokenId` int(10) unsigned NOT NULL AUTO_INCREMENT, `oauthApplicationId` int(10) unsigned NOT NULL, `token` varchar(32) COLLATE utf8_unicode_ci NOT NULL, `scope` text COLLATE utf8_unicode_ci NOT NULL, `createdAt` decimal(12,0) unsigned NOT NULL, PRIMARY KEY (`oauthRefreshTokenId`), KEY `oauthApplicationId` (`oauthApplicationId`), KEY `token` (`token`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
