module.sql:
DROP TABLE IF EXISTS `<%?prefix%>shop_shipping_countries`;
CREATE TABLE `<%?prefix%>shop_shipping_countries` (
  `shippingcountryId` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `shippingzoneId` int(11) unsigned NOT NULL DEFAULT '0',
  `countryId` int(4) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`shippingcountryId`),
  KEY `shippingzoneId` (`shippingzoneId`),
  KEY `countryId` (`countryId`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

DROP TABLE IF EXISTS `<%?prefix%>shop_shipping_grades`;
CREATE TABLE `<%?prefix%>shop_shipping_grades` (
  `shippinggradeId` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(100) COLLATE utf8_unicode_ci NOT NULL DEFAULT '',
  `quantity` float(6,3) unsigned NOT NULL DEFAULT '0.000',
  PRIMARY KEY (`shippinggradeId`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

DROP TABLE IF EXISTS `<%?prefix%>shop_shipping_options`;
CREATE TABLE `<%?prefix%>shop_shipping_options` (
  `shippingoptionId` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(100) COLLATE utf8_unicode_ci NOT NULL DEFAULT '',
  `price` varchar(10) COLLATE utf8_unicode_ci NOT NULL DEFAULT '',
  PRIMARY KEY (`shippingoptionId`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

DROP TABLE IF EXISTS `<%?prefix%>shop_shipping_prices`;
CREATE TABLE `<%?prefix%>shop_shipping_prices` (
  `shippingpriceId` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `shippingzoneId` int(11) unsigned NOT NULL DEFAULT '0',
  `shippinggradeId` int(11) unsigned NOT NULL DEFAULT '0',
  `price` varchar(10) COLLATE utf8_unicode_ci NOT NULL DEFAULT '',
  PRIMARY KEY (`shippingpriceId`),
  KEY `shippingzoneId` (`shippingzoneId`),
  KEY `shippinggradeId` (`shippinggradeId`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

DROP TABLE IF EXISTS `<%?prefix%>shop_shipping_zones`;
CREATE TABLE `<%?prefix%>shop_shipping_zones` (
  `shippingzoneId` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(200) COLLATE utf8_unicode_ci NOT NULL DEFAULT '',
  PRIMARY KEY (`shippingzoneId`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
