DROP TABLE IF EXISTS `<%?prefix%>graphs`; CREATE TABLE IF NOT EXISTS `<%?prefix%>graphs` ( `graphId` int(10) unsigned NOT NULL AUTO_INCREMENT, `type` varchar(20) COLLATE utf8_unicode_ci DEFAULT 'digraph', `rankdir` varchar(3) COLLATE utf8_unicode_ci DEFAULT 'LR', `title` varchar(50) COLLATE utf8_unicode_ci NOT NULL, `description` text COLLATE utf8_unicode_ci, `nodeShape` varchar(30) COLLATE utf8_unicode_ci DEFAULT 'box', `nodeStyle` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL, `nodeColor` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL, `nodeFillcolor` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL, `nodeWidth` float DEFAULT NULL, `nodeHeight` float DEFAULT NULL, `nodeFontsize` float DEFAULT NULL, `nodeFontcolor` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL, `edgeArrowhead` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL, `edgeArrowsize` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL, `edgeColor` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL, `edgeFontcolor` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL, `edgeFontsize` float DEFAULT NULL, `dot` text COLLATE utf8_unicode_ci, `image` longblob, `createdAt` decimal(12,0) NOT NULL, `modifiedAt` decimal(12,0) DEFAULT NULL, `renderedAt` decimal(12,0) DEFAULT NULL, PRIMARY KEY (`graphId`), KEY `type` (`type`), KEY `rankdir` (`rankdir`), KEY `title` (`title`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>graph_edges`; CREATE TABLE IF NOT EXISTS `<%?prefix%>graph_edges` ( `edgeId` int(10) unsigned NOT NULL AUTO_INCREMENT, `graphId` int(10) unsigned NOT NULL, `fromNodeId` varchar(100) COLLATE utf8_unicode_ci NOT NULL, `toNodeId` varchar(100) COLLATE utf8_unicode_ci NOT NULL, `label` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL, `arrowhead` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL, `arrowsize` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL, `color` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL, `fontcolor` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL, `fontsize` float DEFAULT NULL, PRIMARY KEY (`edgeId`), KEY `graphId` (`graphId`), KEY `fromNodeId` (`fromNodeId`), KEY `toNodeId` (`toNodeId`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>graph_nodes`; CREATE TABLE IF NOT EXISTS `<%?prefix%>graph_nodes` ( `nodeId` int(10) unsigned NOT NULL AUTO_INCREMENT, `graphId` int(10) unsigned NOT NULL, `ID` varchar(100) COLLATE utf8_unicode_ci NOT NULL, `label` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL, `description` text COLLATE utf8_unicode_ci, `shape` varchar(30) COLLATE utf8_unicode_ci DEFAULT NULL, `style` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL, `color` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL, `fillcolor` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL, `width` float DEFAULT NULL, `height` float DEFAULT NULL, `fontsize` float DEFAULT NULL, `fontcolor` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL, PRIMARY KEY (`nodeId`), KEY `graphId` (`graphId`), KEY `ID` (`ID`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

DROP TABLE IF EXISTS `<%?prefix%>graphs`;
DROP TABLE IF EXISTS `<%?prefix%>graph_edges`;
DROP TABLE IF EXISTS `<%?prefix%>graph_nodes`;
