module.sql:
DROP TABLE IF EXISTS `<%?prefix%>links`; CREATE TABLE IF NOT EXISTS `<%?prefix%>links` ( `linkId` int(11) unsigned NOT NULL AUTO_INCREMENT, `url` varchar(250) COLLATE utf8_unicode_ci NOT NULL, `createdAt` decimal(12,0) unsigned NOT NULL, `lastAssignAt` decimal(12,0) unsigned DEFAULT NULL, `lastSearchAt` decimal(12,0) unsigned DEFAULT NULL, PRIMARY KEY (`linkId`), KEY `url` (`url`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>notes`; CREATE TABLE IF NOT EXISTS `<%?prefix%>notes` ( `noteId` int(11) unsigned NOT NULL AUTO_INCREMENT, `userId` int(11) unsigned NOT NULL, `projectId` int(10) unsigned DEFAULT '0', `status` tinyint(1) unsigned NOT NULL, `public` int(1) unsigned DEFAULT '0', `format` varchar(20) COLLATE utf8_unicode_ci DEFAULT 'plaintext', `title` varchar(100) COLLATE utf8_unicode_ci NOT NULL, `content` text COLLATE utf8_unicode_ci, `numberViews` int(10) unsigned NOT NULL, `createdAt` decimal(12,0) unsigned NOT NULL, `modifiedAt` decimal(12,0) unsigned NOT NULL, PRIMARY KEY (`noteId`), KEY `userId` (`userId`), KEY `status` (`status`), KEY `projectId` (`projectId`), KEY `public` (`public`), KEY `format` (`format`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>note_links`; CREATE TABLE IF NOT EXISTS `<%?prefix%>note_links` ( `noteLinkId` int(11) unsigned NOT NULL AUTO_INCREMENT, `noteId` int(11) unsigned NOT NULL, `linkId` int(11) unsigned NOT NULL, `title` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL, `createdAt` decimal(12,0) unsigned NOT NULL, PRIMARY KEY (`noteLinkId`), KEY `noteId` (`noteId`), KEY `linkId` (`linkId`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>note_tags`; CREATE TABLE IF NOT EXISTS `<%?prefix%>note_tags` ( `noteTagId` int(11) unsigned NOT NULL AUTO_INCREMENT, `noteId` int(11) unsigned NOT NULL, `tagId` int(11) unsigned NOT NULL, `createdAt` decimal(12,0) unsigned NOT NULL, PRIMARY KEY (`noteTagId`), KEY `noteId` (`noteId`), KEY `tagId` (`tagId`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>tags`; CREATE TABLE IF NOT EXISTS `<%?prefix%>tags` ( `tagId` int(11) unsigned NOT NULL AUTO_INCREMENT, `content` varchar(20) COLLATE utf8_unicode_ci NOT NULL, `createdAt` decimal(12,0) NOT NULL, PRIMARY KEY (`tagId`), KEY `content` (`content`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>term_log`; CREATE TABLE IF NOT EXISTS `<%?prefix%>term_log` ( `termLogId` int(11) unsigned NOT NULL AUTO_INCREMENT, `userId` int(11) unsigned NOT NULL, `count` decimal(10,0) NOT NULL, `content` text COLLATE utf8_unicode_ci NOT NULL, `createdAt` decimal(12,0) unsigned NOT NULL, `modifiedAt` decimal(12,0) unsigned NOT NULL, PRIMARY KEY (`termLogId`), KEY `userId` (`userId`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

DROP TABLE IF EXISTS `<%?prefix%>links`;
DROP TABLE IF EXISTS `<%?prefix%>notes`;
DROP TABLE IF EXISTS `<%?prefix%>note_links`;
DROP TABLE IF EXISTS `<%?prefix%>note_tags`;
DROP TABLE IF EXISTS `<%?prefix%>tags`;
DROP TABLE IF EXISTS `<%?prefix%>term_log`;
