module.hook:
		$config	= $env->getConfig()->getAll( 'module.js_layer.' );
		$buttonDownload	= $config['button.image.download'] == 'yes' ? 'true' : 'false';
		$buttonInfo		= $config['button.image.info'] == 'yes' ? 'true' : 'false';
		$script	= '
$(document).ready(function(){
	Layer.init();
	Layer.buttonImageDownload = '.$buttonDownload.';
	Layer.buttonImageInfo = '.$buttonInfo.';
	Layer.speedShow = '.(int) $config['speed.show'].';
	Layer.speedHide = '.(int) $config['speed.hide'].';
	Layer.labelButtonPrev = "'.addslashes( $config['button.image.prev.label'] ).'";
	Layer.labelButtonNext = "'.addslashes( $config['button.image.next.label'] ).'";
	Layer.labelButtonInfo = "'.addslashes( $config['button.image.info.label'] ).'";
	Layer.labelButtonLoad = "'.addslashes( $config['button.image.load.label'] ).'";
});';
		$context->js->addScript( $script );
