DROP TABLE IF EXISTS `<%?prefix%>finance_banks`; CREATE TABLE IF NOT EXISTS `<%?prefix%>finance_banks` ( `bankId` int(10) unsigned NOT NULL AUTO_INCREMENT, `userId` int(10) unsigned NOT NULL, `type` varchar(20) COLLATE utf8_unicode_ci NOT NULL, `username` varchar(20) COLLATE utf8_unicode_ci NOT NULL, `password` varchar(20) COLLATE utf8_unicode_ci NOT NULL, `title` varchar(30) COLLATE utf8_unicode_ci NOT NULL, `createdAt` decimal(12,0) unsigned NOT NULL, `modifiedAt` decimal(12,0) unsigned DEFAULT NULL, `updatedAt` decimal(12,0) unsigned DEFAULT NULL, PRIMARY KEY (`bankId`), KEY `userId` (`userId`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>finance_bank_accounts`; CREATE TABLE IF NOT EXISTS `<%?prefix%>finance_bank_accounts` ( `bankAccountId` int(10) unsigned NOT NULL AUTO_INCREMENT, `bankId` int(11) NOT NULL, `type` tinyint(1) unsigned NOT NULL, `currency` varchar(3) COLLATE utf8_unicode_ci NOT NULL, `accountKey` varchar(30) COLLATE utf8_unicode_ci NOT NULL, `title` varchar(30) COLLATE utf8_unicode_ci NOT NULL, `value` float(10,2) NOT NULL, `timestamp` decimal(12,0) unsigned DEFAULT NULL, PRIMARY KEY (`bankAccountId`), KEY `type` (`type`), KEY `currency` (`currency`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>finance_funds`; CREATE TABLE IF NOT EXISTS `<%?prefix%>finance_funds` ( `fundId` int(10) unsigned NOT NULL AUTO_INCREMENT, `userId` int(10) unsigned NOT NULL, `ISIN` varchar(30) COLLATE utf8_unicode_ci NOT NULL, `currency` varchar(3) COLLATE utf8_unicode_ci NOT NULL, `pieces` float(10,4) NOT NULL, `kag` varchar(100) COLLATE utf8_unicode_ci NOT NULL, `title` varchar(100) COLLATE utf8_unicode_ci NOT NULL, `createdAt` decimal(12,0) unsigned NOT NULL, `modifiedAt` decimal(12,0) unsigned DEFAULT NULL, PRIMARY KEY (`fundId`), KEY `userId` (`userId`), KEY `ISIN` (`ISIN`), KEY `currency` (`currency`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `<%?prefix%>finance_fund_prices`; CREATE TABLE IF NOT EXISTS `<%?prefix%>finance_fund_prices` ( `fundPriceId` int(11) NOT NULL AUTO_INCREMENT, `fundId` int(11) NOT NULL, `pieces` float(10,4) unsigned NOT NULL, `price` float(10,4) NOT NULL, `timestamp` decimal(12,0) unsigned NOT NULL, PRIMARY KEY (`fundPriceId`), UNIQUE KEY `fundId` (`fundId`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

